import React from 'react';

import Table from './components/table';
import data from './data';
import SearchPanel from './components/search-panel/search-panel'

import './App.css';

export default class App extends React.Component {

    state = {
        data,
        term: ''
    }

    onSearchChange = (term) => {
        this.setState( {term} )
    };

    search(items, term) {

        if (term.length === 0) {
            return items;
        }

        return items.filter((item) => {
            return item.name.toLowerCase()
                .indexOf(term.toLowerCase()) > -1;
        });
    }

    maxHeight = (data) => {

        let newArr = [];
        data.forEach((el) => {
            newArr.push(Number.parseInt(el.height));
        })

        console.log(Math.max.apply(null, newArr));

        data.forEach((el) => {
            if (el.height == Math.max.apply(null, newArr)) {
                el.name.style.color('red');
            }
        })
    }

    render() {

        const { data, term } = this.state;

        const visiblePersons = this.search(data, term);

        const maxHeight = this.maxHeight(data);

        return (
            <div>
                <SearchPanel
                    onSearchChange={this.onSearchChange} />

                <Table persons={ visiblePersons } height={maxHeight}/>
            </div>
        );
    }
}
